import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import Button from '@mui/material/Button';
import Box from '@mui/material/Box';
import Typography from '@mui/material/Typography';
import jsPDF from 'jspdf';
import api from '../services/api';

function InvoicePrint() {
  const { id } = useParams();
  const [invoice, setInvoice] = useState(null);
  const [items, setItems] = useState([]);
  const [customer, setCustomer] = useState(null);
  const [products, setProducts] = useState([]);

  useEffect(() => {
    async function fetchData() {
      const inv = await api.get(`/invoices/${id}`);
      setInvoice(inv.data);
      console.log('Fetched invoice:', inv.data); // Debug log

      const its = await api.get('/invoice_items');
      const filteredItems = its.data.filter(i => i.invoiceId === Number(id));
      setItems(filteredItems);
      console.log('Fetched and filtered items:', filteredItems); // Debug log

      if (inv.data.customerId) {
        const cust = await api.get(`/customers/${inv.data.customerId}`);
        setCustomer(cust.data);
        console.log('Fetched customer:', cust.data); // Debug log
      } else {
        console.log('No customerId found in invoice'); // Debug log
      }

      // Fetch all products
      const prods = await api.get('/products');
      setProducts(prods.data);
      console.log('Fetched products:', prods.data); // Debug log
    }
    fetchData();
  }, [id]);

  const base64Logo = "data:image/png;base64,";
  const handlePrint = () => {
    const doc = new jsPDF({ unit: 'pt', format: [226, 600] }); // 58mm thermal size
    let y = 30;

    // Add Logo (top-left)

    doc.addImage(base64Logo, 'PNG', 10, y, 40, 40); ;
    y += 45;

    // Business Name
    doc.setFontSize(14).setFont(undefined, 'bold');
    doc.text('NexShopy POS', 60, y - 25);

    // Address
    doc.setFontSize(10).setFont(undefined, 'normal');
    doc.text('Rajhat Delhi Road More,', 10, y);
    y += 12;
    doc.text('Bandel, Hooghly', 10, y);
    y += 12;

    // Contact Info
    doc.text('Phone: 98765 43210', 10, y);
    y += 12;
    doc.text('Email: contact@nexshopy.com', 10, y);
    y += 12;

    // GST Number
    doc.text('GSTIN: 22ABCDE1234F1Z5', 10, y); // Replace with your GST
    y += 20;

    // Invoice Details
    doc.text(`Invoice #: ${invoice.invoiceNumber}`, 10, y);
    y += 15;
    doc.text(`Date: ${invoice.date}`, 10, y);
    y += 15;
    if (customer) {
      doc.text(`Customer: ${customer.name}`, 10, y);
      y += 15;
    }

    // Separator
    doc.setDrawColor(150);
    doc.line(10, y, 210, y);
    y += 10;

    // Table Headers
    doc.setFont(undefined, 'bold');
    doc.text('Qty  Item             Price   Disc   Total', 10, y);
    doc.setFont(undefined, 'normal');
    y += 15;

    // Items
    items.forEach(item => {
      const product = products.find(p => p.id === Number(item.productId));
      const productName = product ? product.name : item.productId;
      const total = (item.quantity * (item.price - item.discount)).toFixed(2);
      doc.text(String(item.quantity), 10, y);
      doc.text(productName, 40, y);
      doc.text(`₹${item.price}`, 110, y, { align: 'right' });
      doc.text(`₹${item.discount}`, 150, y, { align: 'right' });
      doc.text(`₹${total}`, 210, y, { align: 'right' });
      y += 15;
    });

    // Totals
    y += 5;
    doc.line(10, y, 210, y);
    y += 15;
    doc.setFont(undefined, 'bold');
    doc.text(`Total: ₹${invoice.total}`, 210, y, { align: 'right' });
    y += 20;

    // Thank You Footer
    doc.setFontSize(10).setFont(undefined, 'normal');
    doc.text('Thank you for shopping with us!', 10, y);
    y += 12;
    doc.text('Terms: No refunds after 7 days.', 10, y);
    y += 12;

    // Powered by
    doc.setFontSize(9).setFont(undefined, 'italic');
    doc.text('Powered by NexShopy', 10, y);

    // Save PDF
    doc.save(`invoice-${invoice.invoiceNumber}.pdf`);
  };

  if (!invoice) return <Typography>Loading...</Typography>;

  return (
    <Box>
      <Typography variant="h5" gutterBottom>Invoice #{invoice.invoiceNumber}</Typography>
      <Button variant="contained" color="primary" onClick={handlePrint}>Print / Download PDF</Button>
      {/* Optionally render invoice details here for preview */}
    </Box>
  );
}

export default InvoicePrint;
