import React, { useEffect, useState } from 'react';
import Typography from '@mui/material/Typography';
import Box from '@mui/material/Box';
import Button from '@mui/material/Button';
import TextField from '@mui/material/TextField';
import IconButton from '@mui/material/IconButton';
import AddIcon from '@mui/icons-material/Add';
import DeleteIcon from '@mui/icons-material/Delete';
import Autocomplete from '@mui/material/Autocomplete';
import api from '../services/api';

function InvoiceCreate() {
  const [customers, setCustomers] = useState([]);
  const [products, setProducts] = useState([]);
  const [salePersons, setSalePersons] = useState([]);
  const [customerId, setCustomerId] = useState('');
  const [salePersonId, setSalePersonId] = useState('');
  const [items, setItems] = useState([{ productId: '', quantity: 1 }]);
  const [invoiceDiscount, setInvoiceDiscount] = useState(0);
  const [paid, setPaid] = useState(0);
  const [due, setDue] = useState(0);
  const [saving, setSaving] = useState(false);
  const [success, setSuccess] = useState(false);

  useEffect(() => {
    async function fetchData() {
      try {
        const [custRes, prodRes, salePersonRes] = await Promise.all([
          api.get('/customers'),
          api.get('/products'),
          api.get('/sale_persons'),
        ]);
        setCustomers(custRes.data);
        setProducts(prodRes.data);
        setSalePersons(salePersonRes.data);
      } catch (error) {
        console.error("Error fetching data:", error);
      }
    }
    fetchData();
  }, []);

  const handleItemChange = (idx, field, value) => {
    setItems(items.map((item, i) => i === idx ? { ...item, [field]: value } : item));
  };

  const handleAddItem = () => {
    setItems([...items, { productId: '', quantity: 1 }]);
  };

  const handleRemoveItem = (idx) => {
    setItems(items.filter((_, i) => i !== idx));
  };

  const subtotal = items.reduce((sum, item) => {
    const product = products.find(p => p.id === Number(item.productId));
    if (!product) return sum;
    return sum + (product.price * item.quantity);
  }, 0);

  const total = subtotal - invoiceDiscount;

  useEffect(() => {
    setDue(total - paid);
  }, [total, paid]);

  const handleSave = async () => {
    setSaving(true);
    const date = new Date();
    const istDate = new Date(date.toLocaleString("en-US", { timeZone: "Asia/Kolkata" }));
    const formattedDate = istDate.toISOString().slice(0, 10);
    const invoiceNumber = 'INV-' + Date.now();

    const invoiceRes = await api.post('/invoices', {
      invoiceNumber,
      customerId,
      formattedDate,
      total,
      discount: invoiceDiscount,
      paid,
      due,
      salePersonId,
    });

    const invoiceId = invoiceRes.data.id;

    await Promise.all(items.map(item =>
      api.post('/invoice_items', {
        invoiceId,
        productId: item.productId,
        quantity: item.quantity,
        price: products.find(p => p.id === Number(item.productId))?.price || 0,
        discount: 0,
        salePersonId,
      })
    ));

    let totalCommission = 0;
    for (const item of items) {
      const product = products.find(p => p.id === Number(item.productId));
      if (product && product.commission) {
        totalCommission += item.quantity * product.commission;
      }
    }

    const currentSalePerson = salePersons.find(s => s.id === Number(salePersonId));
    await api.put(`/sale_persons/${salePersonId}`, {
      id: currentSalePerson.id,
      name: currentSalePerson.name,
      commission: (currentSalePerson.commission || 0) + totalCommission,
    });

    setSaving(false);
    setSuccess(true);
  };

  return (
    <Box>
      <Typography variant="h4" gutterBottom>Create Invoice</Typography>

      <Autocomplete
        options={customers}
        getOptionLabel={option => option.name || ''}
        value={customers.find(c => c.id === Number(customerId)) || null}
        onChange={(_, newValue) => setCustomerId(newValue ? newValue.id : '')}
        renderInput={params => (
          <TextField {...params} label="Customer" fullWidth margin="normal" />
        )}
        isOptionEqualToValue={(option, value) => option.id === value.id}
      />

      <Autocomplete
        options={salePersons}
        getOptionLabel={option => option.name || ''}
        value={salePersons.find(s => s.id === Number(salePersonId)) || null}
        onChange={(_, newValue) => setSalePersonId(newValue ? newValue.id : '')}
        renderInput={params => (
          <TextField {...params} label="Sale Person" fullWidth margin="normal" />
        )}
        isOptionEqualToValue={(option, value) => option.id === value.id}
      />

      <Typography variant="h6" mt={2}>Items</Typography>
      {items.map((item, idx) => {
        const product = products.find(p => p.id === Number(item.productId));
        const unitPrice = product?.price || 0;
        const itemTotal = unitPrice * item.quantity;

        return (
          <Box key={idx} display="flex" alignItems="center" gap={2} mb={1}>
            <Autocomplete
              options={products}
              getOptionLabel={option => `${option.name} (â‚¹${option.price})`}
              value={product || null}
              onChange={(_, newValue) => handleItemChange(idx, 'productId', newValue ? newValue.id : '')}
              renderInput={params => <TextField {...params} label="Product" sx={{ minWidth: 180 }} />}
              isOptionEqualToValue={(option, value) => option.id === value.id}
            />
            <TextField
              label="Qty"
              type="number"
              value={item.quantity}
              onChange={e => handleItemChange(idx, 'quantity', Number(e.target.value))}
              sx={{ width: 80 }}
            />
            <TextField
              label="Unit Price"
              value={unitPrice}
              disabled
              sx={{ width: 120 }}
            />
            <TextField
              label="Item Total"
              value={itemTotal.toFixed(2)}
              disabled
              sx={{ width: 130 }}
            />
            <IconButton onClick={() => handleRemoveItem(idx)} disabled={items.length === 1}><DeleteIcon /></IconButton>
          </Box>
        );
      })}

      <Button startIcon={<AddIcon />} onClick={handleAddItem} sx={{ mb: 2 }}>Add Item</Button>

      <TextField
        label="Invoice Discount"
        type="number"
        value={invoiceDiscount}
        onChange={e => setInvoiceDiscount(Number(e.target.value))}
        fullWidth
        sx={{ mb: 2 }}
      />

      <TextField
        label="Amount Paid"
        type="number"
        value={paid}
        onChange={e => setPaid(Number(e.target.value))}
        fullWidth
        sx={{ mb: 2 }}
      />

      <TextField
        label="Due Amount"
        type="number"
        value={due}
        disabled
        fullWidth
        sx={{ mb: 2 }}
      />

      <Box mb={2}>
        <Typography>Subtotal: â‚¹{subtotal.toFixed(2)}</Typography>
        <Typography>Invoice Discount: â‚¹{invoiceDiscount.toFixed(2)}</Typography>
        <Typography>Paid: â‚¹{paid.toFixed(2)}</Typography>
        <Typography variant="h6">Total: â‚¹{total.toFixed(2)}</Typography>
        <Typography variant="h6">Due: â‚¹{due.toFixed(2)}</Typography>
      </Box>

      <Button
        variant="contained"
        color="primary"
        onClick={handleSave}
        disabled={saving || !customerId || !salePersonId || items.some(i => !i.productId || !i.quantity)}
      >
        Save Invoice
      </Button>

      {success && <Typography color="success.main" mt={2}>Invoice created successfully!</Typography>}
    </Box>
  );
}

export default InvoiceCreate;
